const navAccordion=()=>{document.addEventListener("click",(e=>{if(e.target.matches(".js-npItemButton"))if(e.target.parentElement.classList.contains("expanded"))e.target.parentElement.classList.remove("expanded");else{const t=document.querySelectorAll(".js-npItem");Array.prototype.forEach.call(t,(e=>{e.classList.remove("expanded")})),e.target.parentElement.classList.add("expanded")}}))};document.addEventListener("click",(e=>{if(e.target.matches(".js-npItemButton"))if(e.target.parentElement.classList.contains("expanded"))e.target.parentElement.classList.remove("expanded");else{const t=document.querySelectorAll(".js-npItem");Array.prototype.forEach.call(t,(e=>{e.classList.remove("expanded")})),e.target.parentElement.classList.add("expanded")}}));